'use strict';

import React, { Component } from "react";

class ReactForecast extends Component {

 constructor(props) {
   super(props);
   this.state = {
     height: 245,
     width: "100%"
   };
 }


 render() {
   const url = '//forecast.io/embed/#lat=' + this.props.latitude + '&lon=' + this.props.longitude + '&name=' + this.props.name + '' + '&color=' + this.props.color + '' + '&font=' + this.props.font + '' + '&units=' + this.props.units || '';

   return React.createElement('iframe', { type: 'text/html', height: this.state.height, width: this.state.width, frameBorder: '0', src: url });
 }
}

export default ReactForecast
